import React, { useState } from "react";

const Formulario = () => {
    const [formData, setFormData] = useState({
        nombres: "",
        apellidos: "",
        pais: "",
        ciudad: "",
    });

    const [ciudadesDisponibles, setCiudadesDisponibles] = useState([]);

    // Lista de ciudades por país.
    const ciudadesPorPais = {
        
        ARGENTINA: ["Buenos Aires", "Córdoba", "Rosario", "Mendoza"],
        BRASIL: ["São Paulo", "Río de Janeiro", "Brasilia", "Salvador"],
        MÉXICO: ["Ciudad de México", "Guadalajara", "Monterrey", "Cancún"],
        CHILE: ["Santiago", "Valparaíso", "Concepción", "La Serena"],
        PERÚ: ["Lima", "Arequipa", "Cusco", "Trujillo"],
        PANAMÁ: ["Panamá", "Colón", "David", "Santiago"],
    };

    const handleChange = (e) => {
        const { name, value } = e.target;

        // Actualiza las ciudades disponibles si se selecciona un país.
        if (name === "pais") {
            setCiudadesDisponibles(ciudadesPorPais[value] || []);
            setFormData({
                ...formData,
                [name]: value,
                ciudad: "", // Reinicia la ciudad seleccionada si cambia el país.
            });
        } else {
            // Convierte a mayúsculas los campos de texto.
            const nuevoValor = (name === "nombres" || name === "apellidos") ? value.toUpperCase() : value;
            setFormData({
                ...formData,
                [name]: nuevoValor,
            });
        }
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        console.log("Datos del formulario:", formData);
    };

    return (
        <form onSubmit={handleSubmit}>
            <label htmlFor="nombres">Nombres:</label>
            <input
                type="text"
                id="nombres"
                name="nombres"
                value={formData.nombres}
                onChange={handleChange}
                required
            />

            <label htmlFor="apellidos">Apellidos:</label>
            <input
                type="text"
                id="apellidos"
                name="apellidos"
                value={formData.apellidos}
                onChange={handleChange}
                required
            />

            <label htmlFor="pais">País:</label>
            <select
                id="pais"
                name="pais"
                value={formData.pais}
                onChange={handleChange}
                required
            >
                <option value="">Selecciona un país</option>
                <option value="COLOMBIA">COLOMBIA</option>
                <option value="ARGENTINA">ARGENTINA</option>
                <option value="BRASIL">BRASIL</option>
                <option value="MÉXICO">MÉXICO</option>
                <option value="CHILE">CHILE</option>
                <option value="PERÚ">PERÚ</option>
                <option value="PANAMÁ">PANAMÁ</option>
            </select>

            {ciudadesDisponibles.length > 0 && (
                <>
                    <label htmlFor="ciudad">Ciudad:</label>
                    <select
                        id="ciudad"
                        name="ciudad"
                        value={formData.ciudad}
                        onChange={handleChange}
                        required
                    >
                        <option value="">Selecciona una ciudad</option>
                        {ciudadesDisponibles.map((ciudad, index) => (
                            <option key={index} value={ciudad}>
                                {ciudad}
                            </option>
                        ))}
                    </select>
                </>
            )}

            <button type="submit">Enviar</button>
        </form>
    );
};

export default Formulario;
